        // let iptContent='';
        // let iptTime = 0;
    // const [iptDate,setIptDate]=useState('');
    // const [iptContent,setIptContent]=useState('');
    // const [iptTime,setIptTime]=useState('');

        const [formDate , setFormData] = useState({
    
        iptDate : '',
        iptContent :'',
        iptTime : ''

