import React, { useRef } from "react";
import Card from "../UI/Card";
import '../LogsForm/LogsForm.css'

const LogsForm = () => {

    const ulRef = useRef();
        
    const addDate =()=>{

        console.log()

    };
    
    const addContent =(event)=>{

        console.log(event);

    };
    const addtime=()=>{

        console.log(ulRef.current.value)

    };
    const formSubminHandler=()=>{
        console.log()


    };



    return (

        <Card className="logs-form">
            <form onSubmit={formSubminHandler}>
                <div>
                    <label htmlFor="date">日期</label>
                    <input onChange={addDate} id="date" type="date" />
                </div>

                <div>
                    <label htmlFor="desc">内容</label>
                    <input ref={ulRef} onChange={addContent} id="desc" type="text" />
                </div>
                <div>
                    <label htmlFor="time">时长</label>
                    <input onChange={addtime} id="time" type="number" />
                </div>
                
                <div>
                    <button id="btn">submit</button>
                </div>
            </form>
        </Card>


    )

}

export default LogsForm;
